sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var rightNow = new GlideDateTime();

		var overdueJDETasks = new GlideRecord('x_58872_needit_jdeit_task');
		overdueJDETasks.addQuery('due_date','<=',rightNow);
		overdueJDETasks.addQuery('state','<',3);
		overdueJDETasks.addQuery('assigned_to',current.assigned_to);

		overdueJDETasks.query();

		// Print to the Message field on a notification 
		var count = overdueJDETasks.getRowCount();
		if(count > 1){
			template.print("In addition to this overdue JDEIt Task, you have " + (overdueJDETasks.getRowCount() - 1) + " more overdue JDEIt Tasks.");
		}

})(current, template, email, email_action, event);
