sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
// then the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var overdueJDETasks = new GlideRecord('x_58872_needit_jdeit_task');
overdueJDETasks.addQuery('due_date','<=',rightNow);
overdueJDETasks.addQuery('state','!=',3);

overdueJDETasks.query();
// Write a log message for each overdue NeedIt Task record
// Generate an event for each overdue NeedIt Task record
while(overdueJDETasks.next()){
	gs.info("Overdue JDEIt Task record = " + overdueJDETasks.number);	gs.eventQueue('x_58872_needit.overdueJDEItTask',overdueJDETasks,overdueJDETasks.number,gs.getUserName());
}
